<?php

function ConfigSave() {
	global $post, $config;

	$result = '<?
return [
    ';
	foreach ($config as $k => $val) {
		if (isset($post[$k])) {
			$result .= '"'.$k.'" => "'.$post[$k].'",
    ';
		} else {
			$result .= '"'.$k.'" => "'.$val.'",
    ';
		}
	}
	$result .= '
];';

file_put_contents('../../pagedot-config.php', $result);

	sleep(2);
	echo json_encode([
		'result' => 'ok',
		'message' => 'Успешно сохранено!',
	]);

}

