function start($name, $dir) {
	if (!file_exists('../../'.$dir.$name)) {
		$file = '../start/'.$name;
		$new_file = '../../'.$dir.$name;
		copy($file, $new_file);
	}
}
function GetForm() {
	global $get, $config;
	$form_id = $get['form_id'];
	if (!file_exists('../../app/form/'.$form_id.'.php')) {
		echo json_encode([
			'id' => $get['form_id'],
			'from_name' => '',
			'from_email' => '',
			'to_email' => '',
			'subject' => '',
			'message' => '',
			'redirect' => '',
		]);
		return;
	}
	$form = include('../../app/form/'.$form_id.'.php');
	echo json_encode($form);
}

